mapper.update:
UPDATE board_reply SET
    writer = #{writer},
    content = #{content}
WHERE reply_id = #{reply_id}
        AND board_id = #{board_id}

mapper.delete:
DELETE FROM board_reply
WHERE board_id = #{board_id}
        AND reply_id = #{reply_id}

mapper.select:
SELECT 
    reply_id, board_id, writer, content, reg_date
FROM board_reply
    WHERE board_id = #{board_id}
    				AND reply_id = #{reply_id}
